var cbpHorizontalMenu;
jQuery(document).ready(function(){
cbpHorizontalMenu = (function()
{

    var jQuerylistItems = jQuery( '.cbp-hrmenu > li.submenu ' ),
        jQuerymenuItems = jQuerylistItems.children( 'a' ),
        jQuerybody = jQuery( 'body' ),
        current = -1;

    function init() {
        jQuerymenuItems.on( 'click', open );
        jQuerylistItems.on( 'click', function( event ) { event.stopPropagation(); } );
    }

    function open( event ) {

        if( current !== -1 ) {
            jQuerylistItems.eq( current ).removeClass( 'cbp-hropen' );
        }

        var jQueryitem = jQuery( event.currentTarget ).parent( 'li' ),
            idx = jQueryitem.index();
        if( current === idx ) {
            jQueryitem.removeClass( 'cbp-hropen' );
            current = -1;
        }
        else {
            jQueryitem.addClass( 'cbp-hropen' );
            current = idx;
            jQuerybody.off( 'click' ).on( 'click', close );
        }

        return false;

    }

    function close( event ) {
        jQuerylistItems.eq( current ).removeClass( 'cbp-hropen' );
        current = -1;
    }

    return { init : init };

})();

});
